// src/components/CartSidebar.tsx (‰øÆÊ≠£Âæå)

import React from "react";
import { CartItem, Option } from "../types";

interface CartSidebarProps {
  cart: CartItem[];
  totalAmount: number;
  onUpdateCart: (
    menuItemId: string,
    quantity: number,
    options?: Option[]
  ) => void;
  onPlaceOrder: () => void;
  onGoToPayment: () => void;
  pendingOrderTotalAmount: number;
}

const CartSidebar: React.FC<CartSidebarProps> = ({
  cart,
  totalAmount,
  onUpdateCart,
  onPlaceOrder,
  onGoToPayment,
  pendingOrderTotalAmount,
}) => {
  const handleUpdateQuantity = (item: CartItem, change: number) => {
    onUpdateCart(item.menuItemId, item.quantity + change, item.selectedOptions);
  };

  return (
    <div className="order-sidebar">
      <h2 className="sidebar-title">üõí ÁèæÂú®„ÅÆÊ≥®Êñá</h2>
      {cart.length === 0 ? (
        <p className="empty-cart-message">ÂïÜÂìÅ„ÅåÈÅ∏Êäû„Åï„Çå„Å¶„ÅÑ„Åæ„Åõ„Çì„ÄÇ</p>
      ) : (
        <ul className="cart-list">
          {cart.map((item) => (
            <li key={item.id} className="cart-item">
              <div className="cart-item-info">
                <span className="item-name">{item.name}</span>
                {item.selectedOptions && item.selectedOptions.length > 0 && (
                  <span className="item-options">
                    {item.selectedOptions.map((o: Option) => o.name).join(", ")}
                  </span>
                )}
              </div>
              <div className="item-control">
                <button
                  className="cart-qty-btn"
                  onClick={() => handleUpdateQuantity(item, -1)}
                >
                  ‚àí
                </button>
                <span className="item-quantity">{item.quantity}</span>
                <button
                  className="cart-qty-btn"
                  onClick={() => handleUpdateQuantity(item, 1)}
                >
                  Ôºã
                </button>
              </div>
              <span className="item-price">
                ¬•{(item.price * item.quantity).toLocaleString()}
              </span>
            </li>
          ))}
        </ul>
      )}
      <div className="cart-summary">
        <div className="summary-row">
          <span>ÂêàË®à (Á®éËæº)</span>
          <span className="summary-amount">
            ¬•{totalAmount.toLocaleString()}
          </span>
        </div>
        <button
          className="order-confirm-button"
          onClick={onPlaceOrder}
          disabled={cart.length === 0}
        >
          „Åì„ÅÆÂÜÖÂÆπ„ÅßÊ≥®Êñá„ÇíÁ¢∫ÂÆö„Åô„Çã
        </button>
        <button
          className="goto-payment-btn"
          onClick={onGoToPayment}
          disabled={pendingOrderTotalAmount === 0 && cart.length === 0}
        >
          „Åä‰ºöË®à„Å´ÈÄ≤„ÇÄ üí≥
        </button>
      </div>
    </div>
  );
};

export default CartSidebar;
