/* src/components/CartSidebar.tsx */
import React from "react";
import useCartStore from "../store/cartStore";
import { CartItem } from "../types";

interface CartSidebarProps {
  cart: CartItem[];
  totalAmount: number; // ÁèæÂú®„ÅÆ„Ç´„Éº„Éà„ÅÆ„Åø„ÅÆÈáëÈ°ç
  onPlaceOrder: () => void;
  onGoToPayment: () => void; // ‚òÖ„Åì„Çå„Çí‰Ωø„ÅÑ„Åæ„Åô
  pendingOrderTotalAmount: number; // Á¢∫ÂÆöÊ∏à„Åø„ÅÆÈáëÈ°ç
}

const CartSidebar: React.FC<CartSidebarProps> = ({
  cart,
  totalAmount,
  onPlaceOrder,
  onGoToPayment,
  pendingOrderTotalAmount,
}) => {
  const { updateCartItemQuantity, removeFromCart } = useCartStore();

  const handleQuantityChange = (
    index: number,
    currentQty: number,
    delta: number
  ) => {
    const newQuantity = currentQty + delta;
    if (newQuantity <= 0) {
      removeFromCart(index);
    } else {
      updateCartItemQuantity(index, newQuantity);
    }
  };

  // Ë°®Á§∫Áî®„ÅÆÂêàË®àÈáëÈ°çÔºà„Åì„Çå„Åæ„Åß Ôºã „ÅÑ„Åæ„Ç´„Éº„ÉàÔºâ
  const grandTotal = pendingOrderTotalAmount + totalAmount;

  return (
    <div className="cart-sidebar">
      <div className="cart-header">
        <h2 className="cart-title">ÁèæÂú®„ÅÆÊ≥®Êñá</h2>
      </div>

      <div className="cart-items">
        {cart.length === 0 ? (
          <div className="empty-cart-container">
            <div className="empty-cart-icon">üõí</div>
            <p>„Ç´„Éº„Éà„ÅØÁ©∫„Åß„Åô</p>
          </div>
        ) : (
          cart.map((item, index) => (
            <div key={index} className="cart-item">
              <div className="item-info-row">
                <div className="cart-item-info">
                  <span className="item-name">{item.name}</span>
                  {item.selectedOptions && item.selectedOptions.length > 0 && (
                    <span className="item-options">
                      {item.selectedOptions
                        .map((opt) =>
                          typeof opt === "string" ? opt : opt.name
                        )
                        .join(", ")}
                    </span>
                  )}
                </div>
                <div className="item-price">
                  ¬•{(item.totalPrice || 0).toLocaleString()}
                </div>
              </div>

              <div className="item-controls-row">
                <div className="quantity-adjuster">
                  <button
                    className="qty-btn"
                    onClick={() =>
                      handleQuantityChange(index, item.quantity, -1)
                    }
                  >
                    -
                  </button>
                  <span className="item-qty-val">{item.quantity}</span>
                  <button
                    className="qty-btn"
                    onClick={() =>
                      handleQuantityChange(index, item.quantity, 1)
                    }
                  >
                    +
                  </button>
                </div>
                <button
                  className="remove-link"
                  onClick={() => removeFromCart(index)}
                >
                  ÂâäÈô§
                </button>
              </div>
            </div>
          ))
        )}
      </div>

      <div className="cart-footer">
        {/* ÈáëÈ°çÊÉÖÂ†±„ÅÆË°®Á§∫ */}
        <div
          style={{
            marginBottom: "15px",
            padding: "15px",
            backgroundColor: "#f8f9fa",
            borderRadius: "10px",
            border: "1px solid #eee",
          }}
        >
          {/* Ê≥®ÊñáÊ∏à„Åø„ÅÆÈáëÈ°ç„Åå„ÅÇ„Çå„Å∞Ë°®Á§∫ */}
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "5px",
              color: "#666",
              fontSize: "0.9rem",
            }}
          >
            <span>Ê≥®ÊñáÊ∏à„Åø</span>
            <span>¬•{pendingOrderTotalAmount.toLocaleString()}</span>
          </div>

          {/* „Ç´„Éº„Éà„ÅÆÈáëÈ°ç */}
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              marginBottom: "10px",
              color: "#666",
              fontSize: "0.9rem",
              borderBottom: "1px dashed #ccc",
              paddingBottom: "5px",
            }}
          >
            <span>Ôºã „Ç´„Éº„Éà</span>
            <span>¬•{totalAmount.toLocaleString()}</span>
          </div>

          {/* „ÅäÊîØÊâï„ÅÑÁ∑èÈ°ç */}
          <div
            style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
            }}
          >
            <span
              style={{ fontWeight: "bold", fontSize: "1.1rem", color: "#333" }}
            >
              ÂêàË®à
            </span>
            <span
              style={{
                fontWeight: "bold",
                fontSize: "1.5rem",
                color: "#d32f2f",
              }}
            >
              ¬•{grandTotal.toLocaleString()}
            </span>
          </div>
        </div>

        {/* ‚òÖ„Åì„Åì„Çí‰øÆÊ≠£: „Éú„Çø„É≥„Çí2„Å§„Å´ÂàÜ„Åë„Åæ„Åó„Åü */}
        <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
          {/* 1. Ê≥®ÊñáÁ¢∫ÂÆö„Éú„Çø„É≥ („Ç´„Éº„Éà„Å´Áâ©„Åå„ÅÇ„ÇãÊôÇ„Å†„ÅëÊäº„Åõ„Çã) */}
          <button
            className="place-order-btn"
            disabled={cart.length === 0}
            onClick={onPlaceOrder}
            style={{
              // „Ç´„Éº„Éà„ÅåÁ©∫„Å™„ÇâÁõÆÁ´ã„Åü„Å™„Åè„ÄÅÂÖ•„Å£„Å¶„ÅÑ„Çå„Å∞ÁõÆÁ´ã„Å§Ëâ≤„Å´
              backgroundColor: cart.length > 0 ? "#111" : "#e0e0e0",
              color: cart.length > 0 ? "#fff" : "#a0a0a0",
              cursor: cart.length > 0 ? "pointer" : "default",
              fontWeight: "bold",
              padding: "15px",
              borderRadius: "8px",
              border: "none",
              fontSize: "1rem",
            }}
          >
            {cart.length > 0
              ? "Ê≥®Êñá„ÇíÁ¢∫ÂÆö„Åô„Çã"
              : "ËøΩÂä†„Åô„ÇãÂïÜÂìÅ„ÇíÈÅ∏„Çì„Åß„Åè„Å†„Åï„ÅÑ"}
          </button>

          {/* 2. „Åä‰ºöË®à„Éú„Çø„É≥ (Ê≥®ÊñáÊ∏à„Åø„ÅÆÈáëÈ°ç„Åå„ÅÇ„ÇãÊôÇ„Å†„ÅëË°®Á§∫) */}
          {pendingOrderTotalAmount > 0 && (
            <button
              onClick={onGoToPayment}
              style={{
                backgroundColor: "#d32f2f", // Ëµ§Ëâ≤„Åß„Äå„Åä‰ºöË®à„ÄçÊÑü„ÇíÂá∫„Åô
                color: "white",
                padding: "15px",
                borderRadius: "8px",
                border: "none",
                fontSize: "1.1rem",
                fontWeight: "bold",
                cursor: "pointer",
                marginTop: "5px",
                boxShadow: "0 2px 5px rgba(211, 47, 47, 0.3)",
              }}
            >
              „Åä‰ºöË®à„Å∏ÈÄ≤„ÇÄ (¬•{pendingOrderTotalAmount.toLocaleString()})
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default CartSidebar;
